package org.netbeans.rest.application.config;

import java.util.Set;
import javax.ws.rs.core.Application;

/**
 *
 * @author the-ramones
 */
@javax.ws.rs.ApplicationPath("webresources")
public class ApplicationConfig extends Application {

    @Override
    public Set<Class<?>> getClasses() {
        return getRestResourceClasses();
    }

    /**
     * Do not modify this method. It is automatically generated by NetBeans REST
     * support.
     */
    private Set<Class<?>> getRestResourceClasses() {
        Set<Class<?>> resources = new java.util.HashSet<Class<?>>();
        resources.add(lazy.rest.suppliersXml.class);
        resources.add(lazy.rest.CustomerXml.class);
        resources.add(service.CompanyFacadeREST.class);
        resources.add(lazy.rest.CustomerJson.class);
        resources.add(lazy.rest.SupplierJson.class);
        resources.add(service.CustomerFacadeREST.class);
        resources.add(service.SupplierFacadeREST.class);
        return resources;
    }
}
